import React from "react";
import './SignUp.css'
import login_keys from "../../images/login_keys-removebg.png";
import {useForm} from "react-hook-form";
import {Link} from "react-router-dom";
import AuthAPI from "../../services/AuthAPI";
import {useCookies} from "react-cookie";

export default function SignUp() {
    return (
        <div className='signup'>
            <div className="signup-header">
                Virtual closet
            </div>
            <img
                className="login-keys"
                src={login_keys}
            >
            </img>
            <SignUpBlock/>
        </div>
    )
}

function SignUpBlock(props) {
    const {
        register,
        handleSubmit,
        watch,
        formState: {errors}
    } = useForm();

    function RegisterUser() {
        const login = document.getElementById('login').textContent;
        const password = document.getElementById('password').textContent;
        const password_again = document.getElementById('password_again').textContent;
        const email = document.getElementById('email').textContent;

        console.log(login, password)

        AuthAPI.RegisterUser(login, email, password).then(data => {
            // if (data.status === 201) {
            //     // eslint-disable-next-line react-hooks/rules-of-hooks
            //     const [cookies, setCookie] = useCookies(['access_token', 'refresh_token'])
            //     setCookie("id", data.data.user.id, {path: "/"});
            //     setCookie("access", data.data.access, {path: "/"});
            //     setCookie("refresh", data.data.refresh, {path: "/"});
            //     setCookie("email", email, {path: "/"})
            // } else if (data.status === 400) {
            //     const text = "Fill all the forms!!!";
            // }
            console.log(data.data)

        })

    }


    return (
        <div className='signup-block'>
            <div className="signup-block-login">
                <div className="signup-block-login-title">
                    Login:
                </div>
                <div className='signup-block-login-form'>
                    <input
                        id="login"
                        className="signup-block-login-form-input"
                        defaultValue="test"
                        {...register("example")} />
                </div>
            </div>
            <div className="signup-block-login">
                <div className="signup-block-login-title">
                    Password:
                </div>
                <div className='signup-block-login-form'>
                    <input
                        id='password'
                        className="signup-block-login-form-input"
                        defaultValue="test"
                        {...register("example")} />
                </div>
            </div>
            <div className="signup-block-login">
                <div className="signup-block-login-title">
                    Repeat password:
                </div>
                <div className='signup-block-login-form'>
                    <input
                        id='password_again'
                        className="signup-block-login-form-input"
                        defaultValue="test"
                        {...register("example")} />
                </div>
            </div>
            <div className="signup-block-login">
                <div className="signup-block-login-title">
                    E-mail:
                </div>
                <div className='signup-block-login-form'>
                    <input
                        id='email'
                        className="signup-block-login-form-input"
                        defaultValue="test"
                        {...register("example")} />
                </div>
            </div>
            <button className={'signup-block-button'}
                    onClick={RegisterUser}
            >
                <div className={'signup-block-button-text'}>
                    {/*<Link to='/'>*/}
                        Sign up
                    {/*</Link>*/}

                </div>
            </button>
        </div>
    );
}
